/* OLD CSS VARS
#root.root {--bg: #1c1c1c;}
*/

#root {
  --tabs-font: 10pt Segoe UI;
  --tabs-count-font: .625rem Segoe UI;
  --bookmarks-bookmark-font: .875rem Segoe UI;
  --bookmarks-folder-font: 10pt Segoe UI;
}

/* Adjust styles according to sidebar width */
@media screen and (max-width: 49px) {
  #root {
    --tabs-indent: unset;
  }
  .ScrollBox > .scroll-container {
    overflow: hidden;
  }
  .Tab .audio-icon {
    left: 5px;
		top: 16px;
    transform: scale(.80);
    transform: translateY(0px);
    z-index: 99 !important;
  }
  .Tab .title {
    visibility: collapse;
  }
}

@media screen and (min-width: 49px) {
  .Tab .audio-icon {
    left: 5px;
  }
}


/*
 * Add margins and rounding around tabs
 */

#root {
  --tabs-height: 43px;
}

/* Background layer */

.Tab {
  margin: 0 4px;
  width: unset;
}
.Tab .lvl-wrapper:after {
  content: '';
  position: absolute;
  top: 4px;
  width: 100%;
  height: calc(100% - 5px);
  border-radius: 4px;
  z-index: -1;
}

@media (prefers-color-scheme:light) {
  #root {
    --tabs-activated-bg: white !important;
    --tabs-bg-active: var(--tabs-activated-bg) !important;
    --tabs-selected-fg: var(--tabs-activated-fg) !important;
    --tabs-selected-bg: var(--tabs-activated-bg) !important;
    --bg: #f0f0f0 !important;
  }
  .Tab[data-selected] .lvl-wrapper:after,
  .Tab[data-active] .lvl-wrapper:after {
    box-shadow: 0 0 1px rgba(128,128,142,0.9), 0 0 4px rgba(128,128,142,0.5);
  }
}


/* Reset default styles */
.Tab:hover,
.Tab:active,
.Tab[data-active],
.Tab[data-active]:active,
.Tab[data-selected],
.Tab[data-selected]:hover,
.Tab[data-selected]:active {
  background: transparent;
}

/* Reapply styles */

.Tab:hover .lvl-wrapper:after {
  background-color: var(--tabs-bg-hover);
}

.Tab:active .lvl-wrapper:after,
.Tab[data-active]:active .lvl-wrapper:after {
  background-color: var(--tabs-bg-active);
}

.Tab[data-active] .lvl-wrapper:after {
  background-color: var(--tabs-activated-bg);
}


.Tab[data-selected] .lvl-wrapper:after {
  background-color: var(--tabs-selected-bg);,
}

/* Resize and reposition favicons */
.Tab .fav-icon {
	top: -1px;
  width: 18px;
  height: 18px;
  margin-left: 2.5px;
}

.Tab .placeholder > svg {
  width: 18px;
  height: 18px;
}

.Tab .fav,
.Tab .placeholder,
.Tab .t-box {
	margin-left: 10px;
  margin-bottom: 1px;
	--audio-btn-offset: 5px;
}

.Tab .exp {
	top: -4.5px;
	left: 0px;

}	

.Tab .child-count {
	right: -12px;
	bottom: -11px;
}

.Tab .title {
	bottom: 1px;
}

.Tab .badge {
right: -5px;
}

/* Some Bullshit I stole to make the active tab color a gradient and have slightly rounded corners*/
#root {

        --color-1:rgba(250, 90, 106,1);
        --color-1-t:rgba(250, 90, 106,.3);
        --color-bg:rgba(237, 12, 34,.7);
        --color-gradient-2:rgba(250, 90, 106,1);
        }

        .Tab[data-active="true"] .body {
        background-color: var(--color-bg);
        background: linear-gradient(120deg, var(--color-bg) 20%, var(--color-gradient-2) 180%);
				box-shadow: 0px 0px 3px #00000080;
        border-radius:4px;
        }

				.Tab[data-selected="true"] .body {
				box-shadow: 0px 0px 3px #00000080;
				border-radius: 4px;
				}

